mapper.update:
update lesson l inner join curriculum c on l.lesson_no = c.lesson_no
c.curr_lesson_st=#{curriculum.curriculumLessonStartTime}, c.curr_lesson_et=#{curriculum.curriculumLessonEndTime}, c.curr_lesson_dt=#{curriculum.curriculumLessonDay}, l.sdt=#{startDate}, l.edt=#{endDate}
where l.lesson_no = #{lessonNo}

update curr_lesson_conts
lesson_conts=#{clc.lessonContents}, lesson_days=#{clc.lessonDays},
where curr_lesson_no = #{clc.curriculumLessonNo}

mapper.delete:
    delete from curr_lesson_conts
		where lesson_no = #{lessonNo};

		delete from curriculum
		where lesson_no = #{lessonNo};
		
		delete from lesson
		where lesson_no = #{lessonNo};
